from pathlib import Path
import os
import json

CONFIG = json.load(Path("build.json").open("r",encoding="utf-8"))
def GetEncoding(encoding_name):
    CharSetPath = os.path.join(CONFIG["STORAGE_dir"],"system","CharSet",encoding_name)
    
    return json.load(Path(CharSetPath+".json").open("r",encoding="utf-8"))

