from pathlib import Path
import json

def op_write(data,addres:dict):
    with open(addres["file"],"r+",encoding="utf-8") as data_file:
        data_dict = json.load(data_file)
        data_dict[addres["key"]] = data
        data_file.seek(0)
        data_file.write(json.dumps(data_dict))
        data_file.truncate()